mapper.insert:
 insert into t_user(
     name, phone, password, role
 ) values (
     #{name},#{phone},#{password},#{role}
)

mapper.update:
update t_user


        name = #{name}


        phone = #{phone}


        password = #{password}


        avatar_url = #{avatarUrl}


        update_time = #{updateTime}


where id = #{id}

mapper.select:
select * from t_user


        and name like concat('%',#{name},'%')


        and phone = #{phone}

    and `role` = #{role}

order by create_time desc
